package algoritmo1;

import java.util.Scanner;

public class ex015Ferraduras {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int compraCavalos = sc.nextInt();
		int qtdFerradura = compraCavalos*4;
		System.out.println("Quantidade de ferraduras a ser comprada é "+ qtdFerradura);
		sc.close();

	}

}
